import React from 'react'
import LolliplotNode from './LolliplotNode'
import ZoomArea from './ZoomAreaNode'

let Lolliplot = ({
  height = 250,
  ...props
}) => (
  <div style={{ position: `relative`, height: `${height}px` }}>
    <div style={{ position: `absolute` }}>
      <LolliplotNode
        height={height}
        {...props}
      />
    </div>
    <div style={{ position: `absolute` }}>
      <ZoomArea
        height={height}
        {...props}
      />
    </div>
  </div>
)

export default Lolliplot
